import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import {WebserviceService} from "../service/webservice.service";
import {Router} from "@angular/router";

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent implements OnInit {
  loginForm!: FormGroup;
  loginData: any;

  constructor(private formBuilder: FormBuilder, private websService: WebserviceService, private router:Router) { } // inject WebserviceService here


  ngOnInit(): void {
    this.loginForm = this.formBuilder.group({
      username: ['', Validators.required],
      password: ['', Validators.required]
    });
  }

  onSubmit(): void {
    if (this.loginForm.valid) {
      console.log('Form Submitted!', this.loginForm.value);

      this.websService.verifyLogin(this.loginForm.value.username, this.loginForm.value.password).subscribe(
          (data: any) => {
            console.log(data);
            this.loginData = data; // Salva i dati ricevuti
            if (data.successo === 'ok') {
              this.router.navigate(['/auto']);
            }
          },
          (error: any) => {
            console.log("Errore esecuzione web service post")
            console.log(error)
          }
      );
    }
  }
}
